{!REQUIRESCRIPT("/soap/ajax/42.0/apex.js")}

try{

  var query = "select Id, Name, Ativo__c from Acao_historico_OS__c where Id = '{!Acao_historico_OS__c.Id}'"; 

  var result = sforce.connection.query(query); 
  var records = result.getArray("records"); 

  var myObj = records[0];

  myObj.Ativo__c  = false;

  try{        
    var results = sforce.connection.update([myObj]);
    alert('Registro atualizado com sucesso!');
  } catch (excJS) {
    console.log(excJS);
  }
}catch(excJSConfirm){
  console.log(excJSConfirm);
}
