{!REQUIRESCRIPT("/soap/ajax/38.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/38.0/apex.js")} 

leadConvert();

function leadConvert(){
try {

var rfc = '{!Lead.RFC__c}'; 
var perfil = '{!$Profile.Name}';
var pais = '{!Lead.Country}';

if(rfc == '' && perfil.includes('Mexico')){
alert('{!$Label.MSG_ERRO_0045}');
} else if(pais == '' || pais.toUpperCase() == 'BRASIL'){
alert('{!$Label.MSG_ERRO_0047}');
} else {
var button = document.querySelector("input[name='converter_lead_mexico']");
button.setAttribute("disabled", "disabled");
button.classList.add('btnDisabled');
button.parentElement.innerHTML += '<img style="position: relative;top: 4px;left: 5px;" src="/img/loading.gif"/><span value="carregando"/>';

var res;

res = sforce.apex.execute("Lead_Converter_Mexico", "convertLead", {leadId: '{!Lead.Id}'});

if(res == 'error'){
alert('{!$Label.MSG_ERRO_0046}');
} else {
window.location.href = "/"+res;
}

}

}catch(err) {
if(err.faultstring.includes("duplicate value found")){

var buscaConta = sforce.connection.query("SELECT Id, Name FROM Account WHERE RFC__c ='{!Lead.RFC__c}' limit 1");
records = buscaConta.getArray("records"); 
var idConta = records[0].Id;
var nomeConta = records[0].Name;
var urlConta = 'https://'+window.location.hostname + '/' + idConta;
var textoMensagem = '{!$Label.MSG_ERRO_0048}' + ' \nNombre de cuenta: ' + nomeConta + '\n\nÂ¿Desea acceder a la cuenta?';
if(confirm(textoMensagem)) {

var win = window.open(urlConta, '_blank');

if (win) {

    //Browser has allowed it to be opened
    win.focus();
} else {

    //Browser has blocked it
    alert('Por favor permita las popups para este sitio web');
}


}


} else {
alert(err.faultstring);
}
location.reload();
}

}
